  setTheme(isDark) {
    if (this.tokenStyleElement) {
      this.tokenStyleElement.textContent = `:host { ${DesignTokens.getCompleteTokens(isDark)} }`;
    }
    this.container.dataset.theme = isDark ? "dark" : "light";
  }
