/**
 * Icon name type
 */
export type IconName = keyof typeof Icons;

/**
 * Get icon HTML with custom size
 * 
 * @param name - Icon name
 * @param size - Icon size in pixels (default: 16)
 * @param className - Optional CSS class name
 * @returns SVG HTML string
 * 
 * @example
 * ```typescript
 * const icon = getIcon('bookmark', 24);
 * element.innerHTML = icon;
 * ```
 */
export function getIcon(
    name: IconName,
    size: number = 16,
    className?: string
): string {
    const svg = Icons[name] as string;
    let result = svg;

    // Replace width and height
    result = result.replace(/width="\d+"/, `width="${size}"`);
    result = result.replace(/height="\d+"/, `height="${size}"`);

    // Add class if provided
    if (className) {
        result = result.replace('<svg', `<svg class="${className}"`);
    }

    return result;
}

/**
 * Get icon with specific color
 * 
 * @param name - Icon name
 * @param color - CSS color value
 * @param size - Icon size in pixels (default: 16)
 * @returns SVG HTML string
 * 
 * @example
 * ```typescript
 * const icon = getIconWithColor('bookmark', 'var(--primary-600)', 20);
 * ```
 */
export function getIconWithColor(
    name: IconName,
    color: string,
    size: number = 16
): string {
    let result = getIcon(name, size);
    result = result.replace('<svg', `<svg style="color: ${color}"`);
    return result;
}
